package composite_songgroup;import java.util.Scanner;public class Main {		public static void main(String[] args){				iComposite club = new Composite( "Club" );		iComposite coach = new Composite( "Coach" );		iComposite manager = new Composite( "Manager" );		iComposite medic = new Composite( "Medic" );		iComposite doctors = new Composite( "Doctors" );		iComposite physio = new Composite( "Physiotherapist" );				club.add( coach );		club.add( manager );		club.add( medic );				medic.add( doctors );		medic.add( physio );				Scanner scanner = new Scanner( System.in );		while (true){			System.out.println("1.View Salary\n1.View age\n3.View Commission\n4.Add Staff\n5.Remove Staff\n6.exit\n");			switch (scanner.nextInt()){				case 1: club.displaySalary();				break;				case 2: club.displayAge();					break;				case 3: club.displayComm();					break;				case 4: {					System.out.print("Name: ");					iComposite newStaff = new Leaf(scanner.next());					System.out.print("Age: ");					newStaff.setAge( scanner.nextInt() );					System.out.print("Salary : ");					newStaff.setSal( scanner.nextInt() );					System.out.print("Staff Type : [1/2/3/4] Coach Man Doc Phy ");					int type=scanner.nextInt();					if(type==1)	newStaff.setType( "Coach" );					if(type==2)	newStaff.setType( "Manager" );					if(type==3)	newStaff.setType( "Doctors" );					if(type==4)	newStaff.setType( "Physiotherapist" );					club.add( newStaff );					break;				}				case 5: {					String name = scanner.next();					club.remove( name );					break;				}				case 6:					return;			}		}	}	}